import React from 'react';
import './Home.css'; // Ensure you have this CSS file
import { motion } from 'framer-motion'; // For animations

function Home() {
  return (
    <div className="home">
      <section className="intro">
        <motion.h1 
          initial={{ opacity: 0, y: -50 }} 
          animate={{ opacity: 1, y: 0 }} 
          transition={{ duration: 1 }}>
          Welcome to My Portfolio
        </motion.h1>
        <motion.p 
          initial={{ opacity: 0 }} 
          animate={{ opacity: 1 }} 
          transition={{ duration: 1.5 }}>
          Hi, I'm Asratu, a passionate full-stack software developer with expertise in front-end and back-end technologies.
        </motion.p>
        <motion.a 
          href="/about" 
          className="learn-more"
          initial={{ opacity: 0, y: 50 }} 
          animate={{ opacity: 1, y: 0 }} 
          transition={{ duration: 1, delay: 0.5 }}>
          Learn More About Me
        </motion.a>
      </section>
      <section className="portfolio-preview">
        <h2>My Work</h2>
        {/* Add a brief overview or showcase of your work here */}
      </section>
      {/* Other sections like contact info, testimonials, etc. */}
    </div>
  );
}

export default Home;
