import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import './BlogPage.css'; // Add any specific styles you need

const blogPosts = [
  { id: 1, title: 'Blog Post 1', snippet: 'This is a summary of blog post 1...' },
  { id: 2, title: 'Blog Post 2', snippet: 'This is a summary of blog post 2...' },
  { id: 3, title: 'Blog Post 3', snippet: 'This is a summary of blog post 3...' },
];

function BlogPage() {
  return (
    <div className="blog-page">
      <motion.h1
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        Welcome to My Blog
      </motion.h1>
      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 0.5 }}
      >
        Here you will find insights and updates on various topics.
      </motion.p>
      <div className="blog-posts">
        {blogPosts.map(post => (
          <motion.div
            key={post.id}
            className="blog-post"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <h2>{post.title}</h2>
            <p>{post.snippet}</p>
            <Link to={`/posts/${post.id}`} className="read-more">Read More</Link>
          </motion.div>
        ))}
      </div>
    </div>
  );
}

export default BlogPage;
