import React, { useEffect, useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import "./Header.css";
import ThemeToggle from "./../ThemeToggle/ThemeToggle ";

function Header() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if the user is authenticated by looking for a token
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token from storage
    setIsAuthenticated(false);
    navigate('/login'); // Redirect to login page
  };
  return (
    <header className="gradient-background header">
      <nav className="nav">
        <ul className="nav-links">
          <NavLink
            to="/"
            className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}
          >
            Home
          </NavLink>
          <NavLink
            to="/about"
            className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}
          >
            About
          </NavLink>
          <NavLink
            to="/projects"
            className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}
          >
            Projects
          </NavLink>
          <NavLink
            to="/blog"
            className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}
          >
            Blog
          </NavLink>
          <NavLink
            to="/testimonials"
            className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}
          >
            Testimonials
          </NavLink>
          <NavLink
            to="/contact"
            className={({ isActive }) => (isActive ? "nav-link active" : "nav-link")}
          >
            Contact
          </NavLink>
        </ul>
        {/* <button className="theme-toggle" onClick={ThemeToggle}>Toggle Theme</button> */}
        {isAuthenticated ? (
        <button onClick={handleLogout}>Logout</button>
      ) : (
        <NavLink to="/login">Login</NavLink>
      )}
      </nav>
     

      <ThemeToggle /> {/* Properly aligned toggle button */}
    
    </header>
  );
}

export default Header;
