import React, { useState } from 'react';
import projectsData from '../../data/ProjectsData';
import './Projects.css';

const Projects = () => {
  const [selectedTech, setSelectedTech] = useState('All');

  const handleFilterChange = (tech) => {
    setSelectedTech(tech);
  };

  const filterProjects = (tech) => {
    if (tech === 'All') {
      return projectsData;
    }
    return projectsData.filter(project => project.technologies.includes(tech));
  };

  const technologies = ['All', 'React', 'CSS', 'JavaScript', 'Node.js', 'Express', 'MongoDB', 'Python', 'Django', 'PostgreSQL', 'C#', 'ASP.Net MVC', 'MySQL'];

  return (
    <div className="projects-page">
      <h1>My Projects</h1>
      <div className="filter-buttons">
        {technologies.map(tech => (
          <button
            key={tech}
            onClick={() => handleFilterChange(tech)}
            className={selectedTech === tech ? 'active' : ''}
          >
            {tech}
          </button>
        ))}
      </div>
      <div className="projects-list">
        {filterProjects(selectedTech).map((project) => (
          <div key={project.id} className="project-card">
            <img src={project.image} alt={project.title} className="project-image" />
            <h2 className="project-title">{project.title}</h2>
            <p className="project-description">{project.description}</p>
            <a href={project.link} target="_blank" rel="noopener noreferrer" className="view-link">
              View Project
            </a>
            <div className="project-tech">
              {project.technologies.join(', ')}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Projects;
