import React, { useState } from 'react';
import projectsData from '../../data/ProjectsData';
import ProjectCard from '../../components/ProjectCard/ProjectCard';
const Projects = () => {
  const [selectedTech, setSelectedTech] = useState('All');

  const filterProjects = (tech) => {
    if (tech === 'All') {
      return projectsData;
    }
    return projectsData.filter(project => project.technologies.includes(tech));
  };

  const filteredProjects = filterProjects(selectedTech);

  return (
    <div className="projects-page">
      <h1>My Projects</h1>
      
      <div className="tech-filters">
        <button onClick={() => setSelectedTech('All')}>All</button>
        <button onClick={() => setSelectedTech('React')}>React</button>
        <button onClick={() => setSelectedTech('JavaScript')}>JavaScript</button>
        <button onClick={() => setSelectedTech('Python')}>Python</button>
        {/* Add more buttons for different technologies as needed */}
      </div>
      
      <div className="projects-list">
        {filteredProjects.map(project => (
          <ProjectCard
            key={project.id}
            title={project.title}
            description={project.description}
            image={project.image}
            link={project.link}
            technologies={project.technologies}
          />
        ))}
      </div>
    </div>
  );
};

export default Projects;
