import React from 'react';
import { Link } from 'react-router-dom';
import './Header.css';
import ThemeToggle from './../ThemeToggle/ThemeToggle ';




function Header() {
  return (
    <header className="header">
      <nav>
        <ul className="nav-links">
          <li><Link to="/">Home</Link></li>
          <li><Link to="/about">About</Link></li>
          <NavLink exact to="/" activeClassName="active" className="nav-link">
          Home
        </NavLink>
        <NavLink to="/projects" activeClassName="active" className="nav-link">
          Projects
        </NavLink>
        <NavLink to="/blog" activeClassName="active" className="nav-link">
          Blog
        </NavLink>
        <NavLink to="/testimonials" activeClassName="active" className="nav-link">
          Testimonials
        </NavLink>
        <NavLink to="/contact" activeClassName="active" className="nav-link">
          Contact
        </NavLink>
        </ul>
      </nav>
      <ThemeToggle /> {/* Properly aligned toggle button */}
    </header>
  );
}

export default Header;
