// src/pages/Home.js

import React from 'react';
import './Home.css'; // Ensure you have this CSS file
import { motion } from 'framer-motion'; // Install framer-motion for animations

function Home() {
  return (
    <div className="home">
      <section className="about-me">
        <motion.h1 
          initial={{ opacity: 0, y: -50 }} 
          animate={{ opacity: 1, y: 0 }} 
          transition={{ duration: 1 }}>
          <section className="intro">
        <h1>Welcome to My Portfolio</h1>
        <p>Hi, I'm Asratu, a full-stack software developer with a passion for creating innovative solutions.</p>
        <a href="/about" className="learn-more">Learn More About Me</a>
      </section>
        </motion.h1>
        <motion.div 
          className="content-container"
          initial={{ opacity: 0 }} 
          animate={{ opacity: 1 }} 
          transition={{ duration: 1.5 }}>
          <motion.div 
            className="text-column"
            initial={{ opacity: 0, x: -50 }} 
            animate={{ opacity: 1, x: 0 }} 
            transition={{ duration: 1, delay: 0.5 }}
          >
            <motion.p 
              initial={{ opacity: 0 }} 
              animate={{ opacity: 1 }} 
              transition={{ duration: 1.5 }}>
              Hello! I'm <span className="highlight">Asratu</span>, a <span className="highlight">full-stack</span> software developer specializing in both front-end and back-end technologies.
              My passion for <span className="highlight">innovative solutions</span> drives me to continuously learn and grow in this field.
            </motion.p>
            <div className="marquee-text">
              <span>I am Asratu</span>
            </div>
          </motion.div>
          <motion.div 
            className="profile-column"
            initial={{ opacity: 0, x: 50 }} 
            animate={{ opacity: 1, x: 0 }} 
            transition={{ duration: 1, delay: 0.5 }}
            whileHover={{ scale: 1.05, rotate: 2 }} // Add hover animation
            whileTap={{ scale: 0.95 }} // Add tap animation
          >
            <motion.img 
              src="../../images/profile.jpg" 
              alt="Profile" 
              className="profile-image"
              initial={{ scale: 0.9 }} 
              animate={{ scale: 1 }} 
              transition={{ duration: 1, delay: 0.5 }}
            />
            <motion.p 
              initial={{ opacity: 0 }} 
              animate={{ opacity: 1 }} 
              transition={{ duration: 2, delay: 1 }}>
              I have <span className="highlight">9 years</span> of experience as a <span className="highlight">full-stack developer</span>. My journey includes working on various projects and honing my skills in <span className="highlight">React, Django, Node.js, Express, and C#</span>.
            </motion.p>
          </motion.div>
        </motion.div>
      </section>
    </div>
  );
}

export default Home;
