// src/pages/Home.js

import React from 'react';
import './Home.css'; // Ensure you have this CSS file
import { motion } from 'framer-motion'; // Install framer-motion for animations

function Home() {
  return (
    <div className="home">
      <section className="about-me">
        <motion.h1 
          initial={{ opacity: 0, y: -50 }} 
          animate={{ opacity: 1, y: 0 }} 
          transition={{ duration: 1 }}>
          About Me
        </motion.h1>
        <motion.p 
          initial={{ opacity: 0 }} 
          animate={{ opacity: 1 }} 
          transition={{ duration: 1.5 }}>
          Hello! I'm Asratu, a <span className="highlight">full-stack</span> software developer specializing in both front-end and back-end technologies.
          My passion for <span className="highlight">innovative solutions</span> drives me to continuously learn and grow in this field.
        </motion.p>
        <motion.div 
          className="profile"
          initial={{ opacity: 0, scale: 0.8 }} 
          animate={{ opacity: 1, scale: 1 }} 
          transition={{ duration: 1.5, delay: 0.5 }}>
          <img src="/path-to-your-profile-image.jpg" alt="Profile" className="profile-image" />
          <motion.p 
            initial={{ opacity: 0 }} 
            animate={{ opacity: 1 }} 
            transition={{ duration: 2, delay: 1 }}>
              I have <span className="highlight">9 years</span> of experience as a <span className="highlight">full-stack developer</span>. My journey includes working on various projects and honing my skills in <span className="highlight">React, Django, Node.js, Express, and C#</span>.
          </motion.p>
        </motion.div>
      </section>
    </div>
  );
}

export default Home;
