import React from 'react';
import blogData from '../../data/BlogData';
import './BlogPage.css'; // Ensure you have this CSS file

const BlogPage = () => {
  return (
    <div className="blog-page">
      <h1 className="page-title">Blog Articles</h1>
      <div className="blog-list">
        {blogData.map((article) => (
          <div key={article.id} className="blog-card">
            <h2 className="blog-title">{article.title}</h2>
            <p className="blog-summary">{article.summary}</p>
            <a href={article.link} target="_blank" rel="noopener noreferrer" className="read-more">
              Read More
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BlogPage;
