// src/components/ProjectCard.js

import React from 'react';
import './ProjectCard.css'; // We'll create this stylesheet next

function ProjectCard({ project }) {
  const { title, description, image, link, technologies } = project;

  // Define a placeholder image for projects without an image
  const placeholderImage = require('mywebsite/public/images/placeholder.png');

  return (
    <div className="project-card">
      <div className="project-image-wrapper">
        <img
          src={image || placeholderImage}
          alt={title}
          className="project-image"
        />
      </div>
      <div className="project-content">
        <h3 className="project-title">{title}</h3>
        <p className="project-description">{description}</p>
        {technologies && (
          <ul className="project-technologies">
            {technologies.map((tech, index) => (
              <li key={index} className="technology-item">
                {tech}
              </li>
            ))}
          </ul>
        )}
        <a
          href={link}
          className="project-link"
          target="_blank"
          rel="noopener noreferrer"
        >
          View Project
        </a>
      </div>
    </div>
  );
}

export default ProjectCard;
